const apiUrl = "http://127.0.0.1:4000";
const ratesUrl = apiUrl + "/rates";
const fixerServer = 'http://data.fixer.io/api/latest?access_key=ffcc344a3f31700c0020d166fd17ea96';
import { ToastContainer, toast } from 'react-toastify';

toast.configure();

function getFXfromFixer()
{
  fetch(fixerServer)
  .then(res => res.json())
  .then(data => {
    if (data.success) {
      toast.success("Rate saved");
      return data;
    }
      this.fixerData = data;
      this.refreshRatesValues();        
  });

}

function getRates()
{
    return fetch(ratesUrl, {  
        headers:{
          'Content-Type': 'application/json'
        }
      }).then((response) => response.json())
      .then((responseData) => {
        return responseData;
      })
      .catch(error => console.warn(error));
}

function postRate(rateObj)
{
    return fetch(ratesUrl, {
        method: 'POST',
        body: JSON.stringify(rateObj),  
        headers:{
          'Content-Type': 'application/json'
        }
      }).then(dat => {
          console.log(dat);
          return dat.status;

        })
      .catch(error => console.warn(error));
}

export default {
    postRate,
    getRates,
    getFXfromFixer
}
