import React, { useEffect, useState } from "react";
import './App.css';
import FX from './logic/FXtaxes';
import api from './logic/api';
import Rate from './components/Rate';

function App() {

const FXconverter = new FX();
const [selectToValues, setselectToValues] = useState(["USD", "ARS", "BRL"]);
const [ratesList, setRatesList] = useState([]);

function convert() {  
  let amontToConvert = document.querySelector('#amountToconvert').value;
  let pair = document.querySelector('#selectFrom').value + document.querySelector('#selectTo').value;
  let amountConverted = FXconverter.getRate(pair, Number(amontToConvert));
  console.log(amountConverted)
 document.querySelector('#amountConverted').value = amountConverted.convertAmount;
 document.querySelector('#cardFee').innerText = amountConverted.fee;
 document.querySelector('#cardFeeAmount').innerText = amountConverted.feeAmount;
 document.querySelector('#cardMark').innerText = amountConverted.mark; 
}

function clickFrom()
{
  let selectFrom = document.querySelector('#selectFrom');
  switch(selectFrom.value)
  {
    case 'EUR':
      setselectToValues(["USD", "ARS", "BRL"]);
    break;
    case 'USD':
      setselectToValues(["ARS", "BRL"]);
    break;
    case 'BRL':
      setselectToValues(["ARS"]);
    break;
  }

}

function clcikBuy () {
  let newRateObj = {
    pair: {
      from: {
          rate: document.querySelector('#selectFrom').value,
          amount: Number(document.querySelector('#amountToconvert').value)
      },
      to: {
          rate: document.querySelector('#selectTo').value,
          amount: Number(document.querySelector('#amountConverted').value)
      }
  },
  fee: Number(document.querySelector('#cardFee').innerText),
  feeAmount: Number(document.querySelector('#cardFeeAmount').innerText),
  mark: Number(document.querySelector('#cardMark').innerText)
  }
  api.postRate(newRateObj).then(res => console.log(res));
} 

function selectToItemGen(itemName, itemVal)
{
  return (<option value={itemVal}>{itemName}</option>)
}


useEffect( () => {

  api.getRates().then(data => {
    setRatesList(data);
  });

}, []);


  return (
    <div className="container">
      <div className="row mt-5">

        <div className="col-8">
          <div className="card">
            <div className="card-header">
              Rates List
        </div>
            <div className="card-body">
              {
                ratesList.map(rate => <Rate/>)
              }
            </div>
          </div>
        </div>

        <div className="col-4">
          <div className="card">
            <div className="card-header">
              New Rate
        </div>
            <div className="card-body">

              <div className="row">
                <div className="col-12">
                  <div className="input-group mb-3">
                    <div className="input-group-prepend">
                      <select className="custom-select" id="selectFrom">
                        <option value="EUR" onClick={clickFrom}>EUR</option>
                        <option value="USD" onClick={clickFrom}>USD</option>
                        <option value="BRL" onClick={clickFrom}>BRL</option>
                      </select>
                    </div>
                    <input type="number" className="form-control" aria-label="Text input with dropdown button" onChange={convert} id="amountToconvert"/>
                  </div>
                </div>
                <div className="col-12">
                  <div className="d-flex justify-content-center bd-highlight">
                    <h3 className="bd-highlight text-primary">=</h3>
                  </div>
                </div>
                <div className="col-12">
                  <div className="input-group mb-3">
                    <div className="input-group-prepend">
                      <select className="custom-select" id="selectTo">
                        {
                          selectToValues.map(e => selectToItemGen(e, e))
                        }
                      </select>
                    </div>
                    <input type="number" className="form-control" aria-label="Text input with dropdown button" id="amountConverted"/>
                  </div>
                </div>
                <div className="col-12">
                  <div className="row">
                    <p className="col-5 font-weight-bold">Fee:</p>
                    <p className="col-7" id="cardFee"></p>
                  </div>
                  <div className="row">
                    <p className="col-5 font-weight-bold">Fee amount</p>
                    <p className="col-7" id="cardFeeAmount"></p>
                  </div>
                  <div className="row">
                    <p className="col-5 font-weight-bold">Mark-up</p>
                    <p className="col-7" id="cardMark"></p>
                  </div>
                </div>
                <div className="col-12 mt-2">
                  <div className="d-flex justify-content-center bd-highlight">
                    <button type="button" className="btn btn-outline-primary" onClick={clcikBuy}>Buy</button>
                  </div>

                </div>
              </div>

            </div>
          </div>
        </div>

      </div>
    </div>
  );
}

export default App;
